#!/bin/bash
# execute galgo.sh from project directory
# project directory files: galgo.sh, execute.sh, galgo/configurations.py, configs.txt (autogenerated)
source ../newvenv/bin/activate
# Execute one job array per dataset
#python $PWD/galgo/configurations_pareto.py
python $PWD/galgo/eda/umda/configurations_umda.py

CONFIGURATIONS=()
while IFS= read -r line; do
   CONFIGURATIONS+=("$line")
done <$PWD/configs_umda.txt

NAME="monrp-paretos_umda"
JOBS=${#CONFIGURATIONS[@]}
echo "$JOBS"

qsub -J 1-"$JOBS" \
     -N "$NAME" \
     -e "$PWD"/errors/ \
     -o "$PWD"/outputs/ \
     -v FILE="$PWD"/executer_driver_pareto.py,CWD="$PWD",CONFIG_FILE=configs_umda.txt "$PWD"/execute.sh
